import {useState, useContext} from 'react';
import { BookContext } from './Main/BookContext';
import {LoanBookContext} from './Main/LoanBookContext'


const searchBookItems = [
        {
        title: 'this is a new book',
        author: 'kimbi',
        isbn: 458295,
        year: '12-34-2020',
        img:'./img/1.jpeg'
    },
    {
        title: 'A brief History of time',
        author: 'Stephen Hawkings',
        isbn: 59340450,
        year: '12-34-2020'
    },
    {
        title: 'the theory of quantum relativity',
        author: 'Albert Eistein',
        isbn: 38023783,
        year: '12-34-2020'
    },
    {
        title: 'the seven books of mosses',
        author: 'James Mosses',
        isbn: 2342,
        year: '12-34-2020'
    },
    {
        title: 'Teen titans go',
        author: 'Christopher Colombus',
        isbn: 12,
        year: '12-34-2020'
    }
]
function Search(){

    const [allBooks, setBook] = useContext(BookContext);
    const [search, setSearch] = useState('');

    // ---------------------clear content in search input field
    const clear = ()=>{
        setSearch('');
    }

    console.log(searchBookItems);
    // ---------------------loop through the array of books and return searched books
    
    const searchBook = e=>{
        let value = e.target.value;
        setSearch(value);
        
        let filteredBooks = [];

        for(let i = 0; i<searchBookItems.length;i++){
            value = value.toLowerCase();
            const titleSearch = allBooks[i].title.toLowerCase();

            if(titleSearch.includes(value)){
                filteredBooks.push(allBooks[i])
            }
            setBook(filteredBooks)
        }
        

    }
    
    
    return(
        <div className = "searchBar">
            <div className = 'searchdiv'>
            <input onChange = {searchBook} value = {search} type ="text" placeholder = "Search..."></input>
            <div className="cross" draggable ='true' onClick = {() =>clear()} >x</div>
            </div>
        </div>
    )
}
export default Search;
