import React, {useState, useEffect} from 'react'

const TestResize = () => {

    // set document title using use Effect hook
   const [count, setCount] = useState(0);
    useEffect(() => {
        document.title = 'you clicked ' + count;
        const doc = document.querySelector('.div');

        doc.addEventListener('mousedown',mousedown);
        
        function mousedown(e){
            window.addEventListener('mousemove', mousemove);
            window.addEventListener('mouseup', mouseup);
            let prevX = e.clientX;
            let prevY = e.clientY;
    
            function mousemove(e){
                const currentX = e.clientX;
                const currentY = e.clientY;
                doc.style.top = e.clientY+'px';
                doc.style.left = e.clientX+'px';
            }
    
            function mouseup(){
                window.removeEventListener('mousemove', ()=>{
                    doc.style.top = currentY;
                    doc.style.left = currentX
                })
                window.removeEventListener('mousedown', mousedown);
            }
        }
    })


    // function call runs both functions
    const caller =()=>{
        setCount(count=>count +1)
    }
    return (
        <div className="div" onClick ={caller}>{count}</div>
    )
}

export default TestResize
