import Oath from './Oath';
import './Login_signup.css';
const Login = () => {
    const removeLogin = (e)=>{
        e.target.parentElement.style.display = "none"
    }
    return (
        <div className="loginContainer">
            <div className="login-shadow" onClick={removeLogin}></div>
            <form method="POST">
                <div className="title">Please login to continue...</div>
                <input type="text" name ="u_name" placeholder="enter user name..."/>
                <input type="password" name = "u_psd" placeholder="enter user name..."/>
                <div>
                    <Oath />
                </div>
                <input type="submit" className="login" value="Login"/>
                <div className="forgotpsd"><a href="#forgotpassword">Forgot password?.</a> Click the link to reset</div>
            </form>
        </div>
    )
}

export default Login
