        const bookItem = {
            author: '',
            title: ' ',
            isbn: 0,
            img: 'this is an image place holder',
            isClicked: false
        };
        const [clickedBook, setClickedBook] = useContext(BookHoldContext);
        if(clickedBook === null){
            
        }else{;
            const {title, author, isbn} = clickedBook;
            bookItem.title = title;
            bookItem.author = author;
            bookItem.isbn = isbn;
            bookItem.isClicked = true;
        }
        const hide=(e)=>{
            bookItem.isClicked = false;
            console.log(bookItem);
            console.log(e.target);
            
        }
        console.log(clickedBook);

    return (
        <div className={`bookdetail ${bookItem.isClicked ? 'show': 'unshow'}`} onClick ={hide}>
            <div className="shadow"></div>
            <div className="clickedBook"> 
                <h2>{bookItem.title}</h2>
                <p>{bookItem.author}</p>
                <p>{bookItem.img}</p>
                <form>
                    <input type="text" placeholder="enter name"></input>
                    <input type="text" placeholder="Enter id card number"></input>
                    <button>Loan Book</button>
                </form>
            </div>
        </div>
    )
}

