import Search from './Search';
function NavBar({onChange}){

    const changeToLoan=(e)=>{
        const item = e.target.parentElement
        item.classList.add('active');
        item.previousElementSibling.classList.remove('active');
    }
    const changeToBook=(e)=>{
        const item = e.target.parentElement
        item.classList.add('active');
        item.nextElementSibling.classList.remove('active');
        onChange;
    }
    return(
        <div className ="navBar">
            <div className="wrapper">
            <h2 className = "logo">asguard school library</h2>
            <div className = "navLinks">
                <ul>
                    <li className="active" onClick={changeToBook}><a href="#@">All Books</a></li>
                    <li><a href="#@" onClick={changeToLoan}>loans</a></li>
                    <li><a href="#@">recieve books</a></li>
                </ul>
            </div>
            <Search />
            </div>
        </div>
    )
}
export default NavBar;
