import Loan from './Loan';

function Loans({books}){
    return(
        <div id="book">
            <div className="container">
                <table>
                    <tr style={LoanStyle}>
                        <td>Name</td>
                        <td>Title</td>
                        <td>Contact</td>
                        <td>Loan Day</td> 
                        <td style={returnDay}>Return  Day</td>
                    </tr>
                    <tbody>
                    </tbody>
                </table>
                <ul className="bookItem">
                    {books.map((book)=>(
                        <li key={book.personContact}><Loan book={book} /></li>
                    ))}
                </ul>
                
            </div>
        </div>
    )
}

const LoanStyle={
    gridTemplateColumns: '1.5fr 2.3 fr 1fr 1fr 1fr',
}
const returnDay={
    color: 'black',
    background: 'transparent'
}
export default Loans;
