import React, {useState, useEffect} from 'react'

const TestResize = () => {

    // set document title using use Effect hook
   const [count, setCount] = useState(0);
    useEffect(() => {
        document.title = 'you clicked ' + count;
        const doc = document.querySelector('.div');
        console.log(doc);
        doc.addEventListener('mousedown', (e)=>{
            let prevX = e.clientX;
            let prevY = e.clientY;

            window.addEventListener('mousemove', (e)=>{
                let currentX = prevX - e.clientX;
                let currentY = prevX - e.clientY;

                let DOMrec = doc.getBoundingClientRect();
                doc.style.left= DOMrec - e.clientX+'px';
                doc.style.top= DOMrec - e.clientY+'px';
            })
            window.addEventListener('mouseup', ()=>{

            })
        })
    })

    // setting document title using vanilla js query selector
    const fun=()=>{
        const val = document.querySelector('.div').innerHTML;
        document.title = val;
        console.log(val);
    }

    // function call runs both functions
    const caller =()=>{
        setCount(count=>count +1)
        fun();
    }
    return (
        <div className="div" onClick ={caller}>{count}</div>
    )
}

export default TestResize
