function Main(){
    
    // const [AllBooks, setAllBooks] = useContext(BookContext);
    
    const LoanedBooks=[
        {
            title: 'this is a new book',
            personName: 'kimbi',
            personContact: 671233443,
            Loandate: '12-34-2020',
            returndate: '12-34-2020'
        },
        {
            title: 'A brief History of time',
            personName: 'Stephen Hawkings',
            personContact: 655270011,
            Loandate: '12-34-2020',
            returndate: '12-34-2020'
        },
        {
            title: 'the theory of quantum relativity',
            personName: 'Albert Eistein',
            personContact: 679500109,
            Loandate: '12-34-2020',
            returndate: '12-34-2020',
        }
    ];
    
    const [showLoaned, setShowLoaned] = useState(true);
    const value = useContext(BookContext)
    return(
        <BookContext.Provider>
            <div id ="main">
                <MainPanel />
                {/* <Books books={AllBooks}/>
                <Loans books={LoanedBooks} />  */}
                {/* <Books books={'value'}/> */}
                {console.log(value)}
                {!showLoaned && <Loans books={LoanedBooks} /> }
                <Letter />
            </div>
        </BookContext.Provider>
    )
}

export default Main;
