import React, {useState, useEffect} from 'react'

const TestResize = () => {

    // set document title using use Effect hook
   const [count, setCount] = useState(0);
    useEffect(() => {
        document.title = 'you clicked ' + count;
        const doc = document.querySelector('.div');
        console.log(doc);
        doc.addEventListener('mousedown', (e)=>{
            let prevX = e.clientX;
            let prevY = e.clientY;

            const mousemove =(e)=>{
                let currentX = prevX - e.clientX;
                let currentY = prevX - e.clientY;
                doc.style.left= e.clientX + 'px';
                doc.style.top= e.clientY + 'px';

                prevX = e.clientX;
                prevY = e.clientY;
            }
            window.addEventListener('mousemove',mousemove());
            // const mousedrop =()=>{
            //     window.removeEventListener('mousemove', mousemove);
            //     doc.removeEventListener('mousedown', mousemove);
            // }
            // window.addEventListener('mouseup', mousedrop());
        })
    })


    // function call runs both functions
    const caller =()=>{
        setCount(count=>count +1)
    }
    return (
        <div className="div" onClick ={caller}>{count}</div>
    )
}

export default TestResize
